mapper.select:
SELECT a.id, a.`name`, #{parentId} pId, '../../assets/images/cloud/my-folder-icon.png' icon, 1 isParent FROM file_catalog a WHERE a.parent_id = CONCAT(IFNULL((SELECT t.parent_id FROM file_catalog t WHERE t.id = #{parentId}), ''), #{parentId}, ',')
AND a.create_id = #{userId}
AND a.id NOT IN (SELECT m.file_id FROM file_catelog_recycle_bin m WHERE m.file_type = '1' AND m.create_id = #{userId}) AND a.delete_flag = #{deleteFlag} ORDER BY a.`name` ASC

SELECT * FROM ( SELECT a.id, a.`name`, #{folderId} pId, '../../assets/images/icon/folder-show.png' icon, 1 isParent, 'folder' type, if((SELECT COUNT(*) FROM file_catalog_share l WHERE l.file_id = a.id AND l.file_type = '1') > 0, 'block', 'none') isShow, 1 `orderBy`, 0 size, a.create_id createId, CONVERT(a.create_time, char) createTime FROM file_catalog a WHERE a.parent_id = CONCAT(IFNULL((SELECT t.parent_id FROM file_catalog t WHERE t.id = #{folderId}), ''), #{folderId}, ',')
AND a.create_id = #{userId}
AND a.id NOT IN (SELECT m.file_id FROM file_catelog_recycle_bin m WHERE m.file_type = '1' AND m.create_id = #{userId}) AND a.delete_flag = #{deleteFlag} UNION ALL SELECT a.id, a.`name`, #{folderId} pId, a.thumbnail icon, 0 isParent, a.type, if((SELECT COUNT(*) FROM file_catalog_share l WHERE l.file_id = a.id AND l.file_type = '2') > 0, 'block', 'none') isShow, 2 `orderBy`, a.size, a.create_id createId, CONVERT(a.create_time, char) createTime FROM file_catelog_papers a WHERE a.parent_id = CONCAT(IFNULL((SELECT t.parent_id FROM file_catalog t WHERE t.id = #{folderId}), ''), #{folderId}, ',')
AND a.create_id = #{userId}
AND a.id NOT IN (SELECT m.file_id FROM file_catelog_recycle_bin m WHERE m.file_type = '2' AND m.create_id = #{userId}) AND a.delete_flag = #{deleteFlag} ) k
ORDER BY ${orderByStr}

SELECT a.id, a.`name`, a.thumbnail, a.address, a.type, a.size, a.size_type sizeType, a.chunk, a.chunk_size chunkSize, a.parent_id parentId, a.delete_flag deleteFlag FROM file_catelog_papers a WHERE a.delete_flag = #{deleteFlag} AND
a.id = #{id}
ORDER BY LENGTH(a.parent_id) ASC
