mapper.select:
SELECT
	a.id
FROM
	plan_project a
WHERE a.project_name = #{projectName}

	AND a.id != #{id}

mapper.update:
UPDATE plan_project
project_name = #{projectName}, project_desc = #{projectDesc},
is_share = #{isShare},
last_update_id = #{lastUpdateId}, last_update_time = #{lastUpdateTime}
WHERE id = #{id}
