select.foreach:
SELECT
	d.id
FROM
	(
		SELECT
			@ids${idx} AS _ids,
			(
				SELECT
					@ids${idx} := GROUP_CONCAT(id)
				FROM
					app_workbench_page
				WHERE FIND_IN_SET(parent_id, @ids${idx})
			) AS cids,
			@l${idx} := @l${idx} + 1 AS level
		FROM
			app_workbench_page,
			(
				SELECT
					@ids${idx} := #{id},
					@l${idx} := 0
			) b
		WHERE @ids${idx} IS NOT NULL
	) i,
	app_workbench_page d
WHERE FIND_IN_SET(d.id, i._ids)
