mapper.update:
UPDATE sys_eve_user_install

	win_bg_pic_vague = #{winBgPicVague},
	win_bg_pic_vague_value = #{winBgPicVagueValue}

WHERE user_id = #{userId}

mapper.select:
SELECT a.user_id id, CONCAT_WS('_', a.job_number, a.user_name) `name`, a.job_id pId, 'isPeople' folderType, 0 isParent, '../../assets/images/people-icon.png' `icon`, a.email `email`, a.phone, a.department_id departmentId FROM sys_eve_user_staff a
a.state = '1' AND LENGTH(a.user_id) > 0 AND a.user_id != ''
AND a.company_id = #{companyId}
AND a.user_id != #{userId}
AND LENGTH(a.email) > 0 AND a.email != ''
AND (LENGTH(a.company_id) > 0 AND a.company_id != '' AND a.company_id != '-')

SELECT a.user_id id, CONCAT_WS('_', a.job_number, a.user_name) `name`, a.department_id pId, 'isPeople' folderType, 0 isParent, '../../assets/images/people-icon.png' `icon`, a.email `email`, a.phone, a.department_id departmentId FROM sys_eve_user_staff a
a.state = '1' AND LENGTH(a.user_id) > 0 AND a.user_id != ''
AND a.company_id = #{companyId}
AND a.department_id = #{departmentId}
AND a.user_id != #{userId}
AND LENGTH(a.email) > 0 AND a.email != ''
