mapper.select:
SELECT a.id, a.menu_name menuName, a.menu_name_en menuNameEn, a.icon menuIcon, a.icon_color menuIconColor, a.icon_bg menuIconBg, a.icon_type menuIconType, a.icon_pic menuIconPic, a.menu_level menuLevel, a.menu_parent_id parentId, a.menu_url menuUrl, CASE a.desktop_id WHEN 'winfixedpage00000000' THEN '默认桌面' ELSE d.desktop_name END desktopName, a.order_num orderNum, a.is_share isShare, (SELECT COUNT(*) FROM sys_eve_role_menu d WHERE d.menu_id = a.id) roleNum, IFNULL(GROUP_CONCAT(c.menu_name separator '->'), '创世菜单') menuParentName, a.create_id createId, CONVERT(a.create_time, char) createTime, a.last_update_id lastUpdateId, CONVERT(a.last_update_time, char) lastUpdateTime FROM sys_eve_menu a LEFT JOIN sys_eve_menu c ON INSTR(CONCAT(',', a.menu_parent_id, ','), CONCAT(',', c.id, ',')) LEFT JOIN sys_eve_desktop d ON a.desktop_id = d.id WHERE 1=1
AND a.menu_name LIKE '%${menuName}%'
AND c.menu_name LIKE '%${parentMenuName}%'
AND a.menu_url LIKE '%${menuUrl}%'
AND a.menu_level = #{menuLevel}
AND a.is_share = #{isShare}
AND a.sys_win_id = #{parentId}
AND a.desktop_id = #{desktopId}
GROUP BY a.id ORDER BY a.order_num ASC

mapper.update:
UPDATE sys_eve_menu
order_num = #{orderNum}, last_update_id = #{lastUpdateId}, last_update_time = #{lastUpdateTime},
WHERE id = #{id}

UPDATE sys_eve_menu
order_num = #{orderNum}, last_update_id = #{lastUpdateId}, last_update_time = #{lastUpdateTime},
WHERE id = #{id}
